mapper.insert:
INSERT INTO pea_board(
	  title
	, content
	, uidx
	, ip
	, category
) VALUES(
	  #{title}
	, #{content}
	, #{uidx}
	, #{ip}
	, #{category}
)

mapper.select:
SELECT A.* FROM (select @rownum:=@rownum+1 as rn, b.*,u.name from pea_board b, pea_user u where b.uidx=u.uidx and category=#{category} and (@rownum:=0)=0
AND b.title like concat('%',#{searchValue},'%')
AND b.content like concat('%',#{searchValue},'%') OR u.name like concat('%',#{searchValue},'%')
and b.delyn='N' order by bidx desc )
A WHERE rn LIMIT 5;
A WHERE rn BETWEEN #{start} AND #{end};

SELECT COUNT(*) 
 FROM pea_board
 WHERE category=#{category}

mapper.update:
UPDATE pea_board
 SET title=#{title}
 	,content=#{content}
 	,ip=#{ip}
 	,datetime=now()
 WHERE bidx=#{bidx}

UPDATE pea_board 
 SET delyn='Y' 
WHERE bidx=#{bidx}

mapper.insert:
INSERT INTO pea_comment(
	 bidx
	,content
	,writer
	,ip
	,origincidx
	,uidx
	,depth
) VALUES(
	 #{bidx}
	,#{content}
	,#{writer}
	,#{ip}

		,#{cidx}


		,(SELECT max(cidx)+1 from pea_comment c)

	,#{uidx}
	,#{depth}
)

mapper.update:
UPDATE pea_comment
 SET content=#{content}
 	,datetime=NOW()
 	,ip=#{ip}
WHERE cidx=#{cidx}

UPDATE pea_comment
 SET delyn='Y'
WHERE cidx=#{cidx}
