mapper.select:
SELECT a.mbidx , a.uidx , a.title , CONVERT(a.content USING UTF8) as content , a.writer , a.date , a.ip , a.category , a.delyn , a.secret , b.name , b.profile , b.miniProfile FROM pea_minihome_board a JOIN pea_user b ON a.writer=b.uidx WHERE a.delyn='N' AND a.uidx=#{uidx}
AND a.category=#{category}
AND a.secret is null OR a.secret='N'
ORDER BY a.mbidx DESC
LIMIT 5

mapper.update:
UPDATE pea_minihome_board
 SET delyn='Y'
WHERE mbidx=#{mbidx}

UPDATE pea_minihome_board
 SET title=#{title}
 	,content=#{content}
 	,secret=#{secret}
WHERE mbidx=#{mbidx}

UPDATE pea_user 
 SET miniProfile=#{miniProfile}
WHERE uidx=#{uidx}

 	UPDATE  pea_minihome 
	 SET background=#{background}
	WHERE uidx=#{uidx}
