mapper.update:
UPDATE pea_minihome
   SET h2 	= #{h2}
 WHERE uidx = #{uidx}

UPDATE pea_minihome
   SET h1	= #{h1}
 WHERE uidx = #{uidx}

mapper.select:
	  SELECT subcategory
	    FROM pea_subcategory
	   WHERE uidx		= #{uidx}
	     AND delyn		= 'N' 
    GROUP BY subcategory;

mapper.update:
UPDATE pea_minihome_board a, pea_subcategory b
   SET a.delyn = 'Y',b.delyn = 'Y'
 WHERE subcategory	= #{subcategory}
   AND a.uidx 		= #{uidx}

mapper.insert:
INSERT INTO pea_subcategory
(
	   uidx
	 , subcategory
)
VALUES
(
	   #{uidx}
	 , #{subcategory}
)

mapper.update:
UPDATE pea_subcategory
   SET subcategory	= #{subcategory}
 WHERE uidx = #{uidx}
   AND subcategory = #{content}

mapper.insert:
INSERT INTO pea_friends
(
	   uidx
	 , bfidx
	 , uname
	 , bfname
)
VALUES
(
	   #{uidx}
	 , #{bfidx}
	 , #{uname}
	 , #{bfname}
)
