mapper.select:
SELECT *
 FROM pea_user
WHERE id=#{id}
	AND pwd=#{pwd}

SELECT id 
FROM pea_user
WHERE id=#{id}

SELECT id 
FROM pea_user
WHERE id=#{id}

mapper.insert:
INSERT INTO pea_user(
		  id
		, pwd
		, name
		, birth
		, phone
		, email
		, addr
		, gender
		, ip
)VALUES(
		  #{id}
		, #{pwd}
		, #{name}
		, #{birth}
		, #{phone}
		, #{email}
		, #{addr} 
		, 'W'
		, "123.123.123.123"
)

mapper.select:
