			  price
			, category
			, subcategory
			, name
			, tag
			, img	
			, maker	
			, ip
) 
VALUES(
			  #{price}
			, #{category}
			, 1
			, #{name}
			, #{tag}
			, 'img'
			, 'maker'
			, #{ip}
)
