mapper.insert:
INSERT INTO pea_item(
	  price
	, category
	, subcategory
	, name
	, tag
	, img	
	, maker	
	, ip
) 
VALUES(
	  #{price}
	, #{category}
	, #{subcategory}
	, #{name}
	, #{tag}
	, #{img}
	, #{maker}
	, #{ip}
)

mapper.select:
SELECT * FROM pea_item 
	WHERE delyn='N' and approval='Y' AND postpone='N'
	ORDER BY iidx DESC LIMIT 0,6;

mapper.insert:
INSERT INTO pea_user_item(
			  iidx
			, uidx
			, cart
) 
VALUES(
			  #{iidx}
			, #{uidx}
			, 'Y'
)

mapper.select:
SELECT * FROM pea_user_item 
	WHERE iidx = #{iidx} 
	AND uidx = #{uidx}
	AND (cart='Y' OR buy='Y')
	AND delyn='N'
