mapper.update:
UPDATE pea_minihome
   SET h2 	= #{h2}
 WHERE uidx = #{uidx}

UPDATE pea_minihome
   SET h1	= #{h1}
 WHERE uidx = #{uidx}

mapper.select:
	  SELECT subcategory
	    FROM pea_subcategory
	   WHERE uidx		= #{uidx}
	     AND delyn		= 'N' 
    GROUP BY subcategory;

mapper.update:
UPDATE pea_minihome_board a, pea_subcategory b
   SET a.delyn = 'Y',b.delyn = 'Y'
 WHERE subcategory	= #{subcategory}
   AND a.uidx 		= #{uidx}

mapper.insert:
INSERT INTO pea_subcategory
(
	   uidx
	 , subcategory
)
VALUES
(
	   #{uidx}
	 , #{subcategory}
)

mapper.update:
UPDATE pea_subcategory
   SET subcategory	= #{subcategory}
 WHERE uidx = #{uidx}
   AND subcategory = #{content}

mapper.insert:
INSERT INTO pea_friends
(
	   uidx
	 , bfidx
	 , uname
	 , bfname
)
VALUES
(
	   #{uidx}
	 , #{bfidx}
	 , #{uname}
	 , #{bfname}
)

mapper.update:
UPDATE pea_friends
   SET consent	= 'Y'
 WHERE uidx = #{uidx}
   AND bfidx = #{bfidx}
   AND delyn = 'N'

UPDATE pea_friends
   SET delyn = 'Y'
 WHERE uidx = #{uidx}
   AND bfidx = #{bfidx}

UPDATE pea_friends
   SET delyn = 'Y'
 WHERE uidx = #{uidx}
   AND bfidx = #{bfidx}

mapper.select:
SELECT * 
  FROM pea_friends a
  JOIN (SELECT uidx,name 
  		  FROM pea_user
  		) b ON CASE 
  			   WHEN a.bfidx=b.uidx THEN a.uidx=#{uidx}
  			   WHEN a.uidx=b.uidx THEN a.bfidx=#{uidx}
  			    END
 WHERE (a.uidx = #{uidx} or a.bfidx = #{uidx})
   AND delyn='N'
   AND consent='Y'
 ORDER BY b.name;
