mapper.insert:
INSERT INTO pea_user(
		  id
		, pwd
		, name
		, birth
		, phone
		, email
		, addr
		, gender
		, ip
		, profile
)VALUES(
		  #{id}
		, #{pwd}
		, #{name}
		, #{birth}
		, #{phone}
		, #{email}
		, #{addr} 
		, #{gender}
		, #{ip}
		, '/sweetPea/resources/images/1.png'
)

mapper.select:
SELECT id 
  FROM pea_user
 WHERE email=#{email}
   AND name=#{name}
   AND delyn = 'N'

SELECT uidx 
  FROM pea_user
 WHERE email=#{email}
   AND id=#{id}

mapper.update:
UPDATE pea_user
   SET pwd = #{pwd}
 WHERE uidx = #{uidx}

mapper.select:
SELECT uidx
  FROM pea_user
 WHERE uidx = #{uidx}
   AND pwd = #{pwd}

mapper.update:
UPDATE pea_user
   SET delyn = 'Y'
   , delreason = #{delreason}
 WHERE uidx = #{uidx}

mapper.select:
SELECT uidx
  FROM pea_user
 WHERE uidx = #{uidx}
   AND email = #{email}

mapper.update:
UPDATE pea_user
   SET name   =	#{name}
   	 , gender = #{gender}
   	 , birth  =	#{birth}
   	 , phone  =	#{phone}
   	 , email  =	#{email}
   	 , addr   =	#{addr}
 WHERE uidx   =	#{uidx}

UPDATE pea_user
 SET profile = #{profile}
WHERE uidx = #{uidx}
